var webdriverio = require('./');

var client = webdriverio.remote({
    desiredCapabilities: {
        browserName: 'phantomjs'
    }
});

console.log(client.eventHandler.listeners('error').length);
client.on('error', function() {
    console.log('meee');
});

client.init().url('http://google.com').getTitle().then(function(title) {
    console.log(title);
console.log(this.eventHandler.listeners('error'));
}).end();