var webdriverio = require('./'),
    async = require('async');

var client = webdriverio.multiremote({
    'a': {
        // logLevel: 'verbose',
        desiredCapabilities: {
            browserName: 'phantomjs'
        }
    },
    'b': {
        // logLevel: 'verbose',
        desiredCapabilities: {
            browserName: 'phantomjs'
        }
    }
});

// client = webdriverio.remote({
//     desiredCapabilities: {
//         browserName: 'phantomjs'
//     }
// });

var a = client.select('a');
var b = client.select('b');

client.init().pause(6000).title()

a
    .url('http://google.com')
    .call(function() {
        console.log('hi');
    })
    .getTitle(function(err, title) {
        console.log(title);
    });

b
    .url('http://bromi.de')
    .pause(6000)
    .call(function() {
        console.log('hi');
    })
    .getTitle(function(err, title) {
        console.log(title);
    });

b.end();
a.end();

